mapper.select:
SELECT * FROM tbltype

	
		${column} LIKE #{keyword}
	

ORDER BY parent_type ASC
LIMIT #{start},#{lineSize};

SELECT * FROM tbltype
WHERE locked=0

	and ${column} LIKE #{keyword}

ORDER BY parent_type ASC
LIMIT #{start},#{lineSize};

SELECT COUNT(*) FROM tbltype
WHERE locked=0

	and ${column} LIKE #{keyword}
